export function initEchar (e) {
  if (!e) { return }
  console.log(e)
  let colors = ['#5793f3', '#d14a61', '#675bba', '#ffe400', '#9400D3'];
  // 设置myChart数据
  var myChart = echarts.init(document.getElementById('main'));
  let arr = e.map((item, index) => {
    return lineEchars.re[item.key]
  })
  let grid, xAxis, yAxis, series
  // 设置gird,判断arr长的度是否大于1
  if (arr.length > 1 && arr.some(item => item.type === 'bar')) {
    xAxis = [
      {
        gridIndex: 0,
        type: 'category',
        data: lineEchars.re[1].T,
        position: 'top',
      },
      {
        gridIndex: 1,
        type: 'category',
        data: lineEchars.re[2].T
      }
    ]
    grid = [
      { top: '8%', left: '5%', width: '75%', height: '40%' },
      { top: '55%', left: '5%', width: '75%', height: '36%' }
    ]
    yAxis = arr.map((item, index) => {
      let res = {
        gridIndex: item.type === 'bar' ? 0 : 1,
        name: item.datatype,
        type: 'value',
        axisLine: {
          lineStyle: {
            color: colors[index],
          }
        }
      }
      if (item.type !== 'bar' && index > 2) {
        res['offset'] = (index - 2) * 70
      }
      return res
    })
    series = arr.map((item, index) => {
      return {
        xAxisIndex: item.type === 'bar' ? 0 : 1,
        yAxisIndex: index,
        data: item.V,
        type: item.type,
        itemStyle: {
          color: colors[index],
        }
      }
    })
    console.log('当前为多个布局')
  } else {
    grid = { top: '8%', left: '5%', width: '75%', height: '85%' }

    xAxis = {
      type: 'category',
      data: lineEchars.re[2].T
    }
    yAxis = arr.map((item, index) => {
      let res = {
        name: item.datatype,
        type: 'value',
        axisLine: {
          lineStyle: {
            color: colors[index],
          }
        }
      }
      if (index > 1) {
        res['offset'] = (index - 1) * 70
      }
      return res
    })
    series = arr.map((item, index) => {
      return {
        yAxisIndex: index,
        data: item.V,
        type: item.type,
        itemStyle: {
          color: colors[index],
        }
      }
    })
    console.log('111')
  }

  var optionsX = {
    backgroundColor: '#f2f3f4',
    xAxis,
    yAxis,
    series,
    grid
  };

  // 绘制图表
  myChart.setOption(optionsX, true);
}